inputs.body:
set query_datetimescope_column = "TimeGenerated";
set query_datetimescope_from = datetime(@{items('For_each')?['properties']?['additionalData']?['Query Start Time UTC']});
set query_datetimescope_to = datetime(@{items('For_each')?['properties']?['additionalData']?['Query End Time UTC']});
@{items('For_each')?['properties']?['additionalData']?['Query']}
| top @{variables('EventsNumber')} by TimeGenerated desc
